import { Injectable } from '@angular/core';
import * as csvParser from 'csv-parser';
import { Readable } from 'stream';
import * as Papa from 'papaparse';
import { Observable, map } from 'rxjs';
import { HttpClient, HttpParams } from '@angular/common/http';

@Injectable({
    providedIn: 'root'
})



export class AirlineCodesService {
    readonly AirlineCodes = '../assets/csv/airline-codes.csv';

    constructor(private http: HttpClient) { }

    getCsvData(): Observable<any> {
        return this.http
            .get(this.AirlineCodes, { responseType: 'text' })
            .pipe(map(res => {
                console.log('res ', res)
                return this.parseCSVData(res);
            }));
    }



    public parseCSVData(csvData: string): Promise<any[]> {
        return new Promise((resolve, reject) => {
            Papa.parse(csvData, {
                complete: function (results: { data: any[] | PromiseLike<any[]>; }) {
                    resolve(results.data);
                },
                error: function (error: Error) {
                    reject(error);
                }
            });
        });
    }
}
