import { Component } from '@angular/core';
import html2canvas from 'html2canvas';

@Component({
  selector: 'app-sprite-generator',
  templateUrl: './sprite-generator.component.html',
  styleUrls: ['./sprite-generator.component.css']
})
export class SpriteGeneratorComponent {
  files: File[] = [];
  spriteSheetUrl: string | null = null;

  onFileSelected(event: any): void {
    this.files = Array.from(event.target.files);
  }

  async generateSpriteSheet(): Promise<void> {
    if (this.files.length === 0) {
      alert('Please select some SVG files');
      return;
    }

    const svgs = await Promise.all(this.files.map(file => this.readFileAsText(file)));
    const canvas = document.createElement('canvas');
    const size = 100;  // Size of each sprite (assuming square sprites for simplicity)
    canvas.width = size * svgs.length;
    canvas.height = size;
    const ctx = canvas.getContext('2d');

    await Promise.all(svgs.map((svg, index) => this.drawSvgToCanvas(ctx, svg, index, size)));

    this.spriteSheetUrl = canvas.toDataURL('image/png');
  }

  private readFileAsText(file: File): Promise<string> {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => resolve(reader.result as string);
      reader.onerror = reject;
      reader.readAsText(file);
    });
  }

  private drawSvgToCanvas(ctx: CanvasRenderingContext2D | null, svg: string, index: number, size: number): Promise<void> {
    return new Promise((resolve, reject) => {
      const img = new Image();
      img.onload = () => {
        ctx?.drawImage(img, index * size, 0, size, size);
        resolve();
      };
      img.onerror = reject;
      img.src = `data:image/svg+xml;base64,${btoa(svg)}`;
    });
  }
}
